const BookPage = () => {
  const [selectedGenreId, setSelectedGenreId] = React.useState(null);
  const [availabilityFilter, setAvailabilityFilter] = React.useState("ALL");
  const [searchTerm, setSearchTerm] = React.useState("");
  const handleGenreChange = (event) => {
    const genreId = event.target.value;
    setSelectedGenreId(genreId);
  };

  const [sortBy, setSortBy] = React.useState("createdAt");
  const [sortDirection, setSortDirection] = React.useState("desc");

  const getCurrentSortValue = () => {
    return `${sortBy}-${sortDirection.toLowerCase()}`;
  };

  console.log("Selected Genre ID:", selectedGenreId);
  const handleSortChange = (value) => {
    const [field, direction] = value.split("-");
    setSortBy(field);
    setSortDirection(direction.toLowerCase());
  };
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50">
      <div className="bg-white border-b border-gray-200 text-center">
        <div className="px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-4xl text-gray-900 mb-2">
            <h1 className="font-bold">
              Browse Our{" "}
              <span className="bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
                Collection
              </span>
            </h1>
            <p className="text-lg text-gray-600 ">
              Discover thousands of book across all genres.
            </p>
          </div>
        </div>
      </div>

      <div className="px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          <aside className="lg:w-72 space-y-6">
            <div className="space-y-6">
              <GenreFilter onGenreSelect={handleGenreChange} genres={genres} />
              <div className="bg-white rounded-xl shadow-md mt-20 p-4 border border-gray-100">
                <h3 className="text-lg font-bold text-gray-900 mb-4 pb-3 border-b border-gray-200">
                  Availability
                </h3>
                <FormControl fullWidth>
                  <Select
                    value={availabilityFilter}
                    onChange={(e) => setAvailabilityFilter(e.target.value)}
                  >
                    <MenuItem value={"ALL"}>All Books</MenuItem>
                    <MenuItem value={"AVAILABLE"}>Available Only</MenuItem>
                    <MenuItem value={"CHECKED_OUT"}>Checked Out</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </div>
          </aside>

          <main className="flex-1 space-y-6">
            <div className="flex flex-col md:flex-row gap-4">
              <div className="flex-1">
                <TextField
                  fullWidth
                  placeholder="Search by title, author, or category..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <Search className="text-gray-400" />
                      </InputAdornment>
                    ),
                  }}
                  sx={{
                    "& .MuiOutlinedInput-root": {
                      "&:hover fieldset": {
                        borderColor: "#4F46E5",
                      },
                      "&.Mui-focused fieldset": {
                        borderColor: "#4F46E5",
                      },
                    },
                  }}
                />
              </div>

              <div className="md:w-64">
                <FormControl fullWidth>
                  <InputLabel>Sort By</InputLabel>
                  <Select
                    value={getCurrentSortValue()}
                    onChange={(e) => handleSortChange(e.target.value)}
                    label="Sort By"
                    startAdornment={
                      <InputAdornment position="start">
                        <Sort className="text-gray-400" />
                      </InputAdornment>
                    }
                    sx={{
                      "& .MuiOutlinedInput-notchedOutline": {
                        borderColor: "#E5E7EB",
                      },
                      "&:hover .MuiOutlinedInput-notchedOutline": {
                        borderColor: "#4F46E5",
                      },
                      "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
                        borderColor: "#4F46E5",
                      },
                    }}
                  >
                    <MenuItem value="title-asc">Title (A-Z)</MenuItem>
                    <MenuItem value="title-desc">Title (Z-A)</MenuItem>
                    <MenuItem value="author-asc">Author (A-Z)</MenuItem>
                    <MenuItem value="author-desc">Author (Z-A)</MenuItem>
                    <MenuItem value="createdAt-desc">Newest First</MenuItem>
                    <MenuItem value="createdAt-asc">Oldest First</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {books.map((book) => (
                <BookCard key={book.id} book={book} />
              ))}
            </div>
          </main>
        </div>
      </div>
    </div>
  );
};

export default BookPage;
